  const search = (data) => {
    return data.filter((item) =>
      keys.some((key) => item[key].toLowerCase().includes(query))
    );
  };
return (
  <div className="app">
      <input
        className="search"
    placeholder="Search..."
    onChange={(e) => setQuery(e.target.value.toLowerCase())}
  />
{<Table data={Search(Users)} />}
</div>
);
}
